import React, { Component } from 'react';
import { FormattedMessage } from 'react-intl';
import { connect } from 'react-redux';
import './TableManagePosts.scss';
import * as actions from '../../../store/actions';

class TableManagePosts extends Component {

    constructor(props) {
        super(props);
        this.state = {
            productRedux: []
        };
    };

    componentDidMount() {
        this.props.getAllPosts();
    };

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (prevProps.allProducts !== this.props.allProducts) {
            this.setState({ productRedux: this.props.allProducts })
        }
    };

    handleDeleteProduct = (product) => {
        this.props.deleteProduct(product.id);
    }

    handleEditUser = (product) => {
        this.props.handleEditProductParent(product);
    };

    render() {
        let { productRedux } = this.state;

        return (
            <div className='posts-container mx-0'>
                <table id='TableManagePosts'>
                    <tr>
                        <th>Tiêu đề bài viết</th>
                        <th>Phân loại</th>
                        <th>Ngày tạo</th>
                        <th>Ngày sửa</th>
                        <th>Action</th>
                    </tr>
                    {productRedux && productRedux.length > 0 &&
                        productRedux.map((item, index) => {
                            return (
                                <tr key={index}>
                                    <td>{item.nameProduct}</td>
                                    <td>{item.categoryType}</td>
                                    <td>{item.price}</td>
                                    <td>{item.quantity}</td>
                                    <td>{item.slug}</td>
                                    <td>{item.status}</td>
                                    <td>
                                        <button
                                            type='button'
                                            className='btn-edit'
                                            onClick={() => this.handleEditUser(item)}
                                        ><i className='fas fa-pencil-alt'></i></button>
                                        <button
                                            onClick={() => { this.handleDeleteProduct(item) }}
                                            type='button'
                                            className='btn-delete'
                                        ><i className='fas fa-trash'></i></button>
                                    </td>
                                </tr>
                            )
                        })
                    }
                </table>
            </div>
        );

    }

}

const mapStateToProps = state => {
    return {
        allProducts: state.product.products
    };
};

const mapDispatchToProps = dispatch => {
    return {
        getAllPosts: () => dispatch(actions.getAllPostsStart()),
        deleteProduct: (id) => dispatch(actions.deleteProductStart(id))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(TableManagePosts);
