import React, { Component } from 'react';
import { FormattedMessage } from 'react-intl';
import { connect } from 'react-redux';
import './UserRedux.scss';
import { languages } from '../../../utils';
import * as actions from '../../../store/actions';

class UserRedux extends Component {

    constructor(props) {
        super(props);
        this.state = {
            genderArray: [],
            roleArray: [],
            previewImageURL: ''
        }
    }

    async componentDidMount() {
        this.props.getGenderStart();
        this.props.getRoleStart();
        // try {
        //     let res = await getAllCodeService('gender');
        //     if (res && res.errCode === 0) {
        //         this.setState({
        //             genderArray: res.data
        //         })
        //     }
        //     console.log('check res', res);
        // } catch (e) {
        //     console.log(e);
        // }
    };

    componentDidUpdate(prevProps, prevState, spapshot) {
        if (prevProps.genderRedux !== this.props.genderRedux) {
            this.setState({
                genderArray: this.props.genderRedux
            })
        };

        if (prevProps.roleRedux !== this.props.roleRedux) {
            this.setState({
                roleArray: this.props.roleRedux
            })
        }
    };

    handleOnChangeImage = (event) => {
        let fileImage = event.target.files;
        let file = fileImage[0];
        if (file) {
            let imageObjectUrl = URL.createObjectURL(file);
            this.setState({
                previewImageURL: imageObjectUrl
            })
        }
    };

    render() {
        let genders = this.state.genderArray;
        let roles = this.state.roleArray;
        let language = this.props.language;
        let isLoadingGender = this.props.isLoadingGender;
        console.log('check genderRedux: ', this.props.genderRedux);

        return (
            <div className='user-redux-container'>
                <div className="title" >USER MANAGEMENT USING REDUX</div>
                <div>{isLoadingGender === true ? 'Loading genders' : ""}</div>
                <div className='container'>
                    <div className='row'>
                        <div className='col-12 my-3'><FormattedMessage id="manage-user.add" /></div>
                        <div className='col-3'>
                            <label><FormattedMessage id="manage-user.email" /></label>
                            <input className='form-control' type='email' />
                        </div>
                        <div className='col-3'>
                            <label><FormattedMessage id="manage-user.password" /></label>
                            <input className='form-control' type='password' />
                        </div>
                        <div className='col-3'>
                            <label><FormattedMessage id="manage-user.fullname" /></label>
                            <input className='form-control' type='text' />
                        </div>
                        <div className='col-3'>
                            <label><FormattedMessage id="manage-user.phonenumber" /></label>
                            <input className='form-control' type='text' />
                        </div>
                        <div className='col-8'>
                            <label><FormattedMessage id="manage-user.address" /></label>
                            <input className='form-control' type='text' />
                        </div>
                        <div className='col-4'>
                            <label><FormattedMessage id="manage-user.gender" /></label>
                            <select className="form-control">
                                {genders && genders.length > 0 &&
                                    genders.map((item, index) => {
                                        return (
                                            <option key={index}>
                                                {language === languages.VI ? item.valueVi : item.valueEn}
                                            </option>
                                        )
                                    })
                                }
                            </select>
                        </div>
                        <div className='col-8'>
                            <label><FormattedMessage id="manage-user.image" /></label>
                            <div className='preview-img-container'>
                                <input id='previewImg' type='file' hidden onChange={(event) => this.handleOnChangeImage(event)} />
                                <label className='label-upload' htmlFor="previewImg">Tải ảnh <i className='fas fa-upload'></i></label>
                                <div className='preview-image' style={{ backgroundImage: `url(${this.state.previewImageURL})` }}></div>
                            </div>
                        </div>
                        <div className='col-4'>
                            <label><FormattedMessage id="manage-user.roleid" /></label>
                            <select className="form-control">
                                {roles && roles.length > 0 &&
                                    roles.map((item, index) => {
                                        return (
                                            <option key={index}>
                                                {language === languages.VI ? item.valueVi : item.valueEn}
                                            </option>
                                        )
                                    })
                                }
                            </select>
                        </div>
                        <div className='col-12 mt-3'>
                            <button className='btn btn-primary'><FormattedMessage id="manage-user.create" /></button>
                        </div>
                    </div>
                </div>
            </div>
        )
    }

}

const mapStateToProps = state => {
    return {
        language: state.app.language,
        genderRedux: state.admin.genders,
        roleRedux: state.admin.roles,
        isLoadingGender: state.admin.isLoadingGender
    };
};

const mapDispatchToProps = dispatch => {
    return {
        getGenderStart: () => dispatch(actions.fetchGenderStart()),
        getRoleStart: () => dispatch(actions.fetchRoleStart())
        //processLogout: () => dispatch(actions.processLogout()),
        //changeLanguageAppRedux: (language) => dispatch(actions.changeLanguageApp(language))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(UserRedux);
