import React, { Component } from 'react';
import { connect } from 'react-redux';
import { push } from "connected-react-router";

import * as actions from "../../store/actions";

import './Login.scss';
import { FormattedMessage } from 'react-intl';
import { Form, Input } from 'reactstrap';

class Login extends Component {
    constructor(props) {
        super(props);
    }

    render() {


        return (
            <div className='login-background row'>
                <div className='col-8 login-introduction'></div>
                <div className='col-4 login-form'>
                    <div className='login-container'>
                        <div className='login-content row'>
                            <div className='col-12 title'>Aquatic System</div>
                            <div className=' col-12 signin-text text-center'>Welcome to Aquatic System</div>
                            <Form className='col-12 form'>
                                <div className="col-12 form-group">
                                    <label>Email address:</label>
                                    <input type='text' className='form-control' placeholder='Enter your Username or Email'></input>
                                </div>
                                <div class="col-12 form-group">
                                    <label>Password:</label>
                                    <input type='password' className='form-control' placeholder='Enter your Password'></input>
                                </div>
                                <div className="form-check">
                                    <input type='checkbox'></input>
                                    <label>Remember me</label>
                                </div>
                                <div className='col-12 forgot-password'>Forgot password</div>
                                <div className='col-12 signin'>
                                    <button className='btn btn-primary'>Sign in</button>
                                </div>
                            </Form>
                        </div>
                    </div>
                </div>
            </div>
        );

    };
};

const mapStateToProps = state => {
    return {
        lang: state.app.language
    };
};

const mapDispatchToProps = dispatch => {
    return {
        navigate: (path) => dispatch(push(path)),
        adminLoginSuccess: (adminInfo) => dispatch(actions.adminLoginSuccess(adminInfo)),
        adminLoginFail: () => dispatch(actions.adminLoginFail()),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
