import React, { Component } from 'react';
import { FormattedMessage } from 'react-intl';
import { connect } from 'react-redux';
import './UserRedux.scss';
import { languages } from '../../../utils';
import * as actions from '../../../store/actions';
import LightBox from 'react-image-lightbox';
import 'react-image-lightbox/style.css';

class UserRedux extends Component {

    constructor(props) {
        super(props);
        this.state = {
            genderArray: [],
            roleArray: [],
            previewImageURL: '',
            isOpen: false,

            email: '',
            password: '',
            fullName: '',
            phoneNumber: '',
            address: '',
            gender: '',
            role: '',
            avatar: ''
        }
    }

    async componentDidMount() {
        this.props.getGenderStart();
        this.props.getRoleStart();
        // try {
        //     let res = await getAllCodeService('gender');
        //     if (res && res.errCode === 0) {
        //         this.setState({
        //             genderArray: res.data
        //         })
        //     }
        //     console.log('check res', res);
        // } catch (e) {
        //     console.log(e);
        // }
    };

    componentDidUpdate(prevProps, prevState, spapshot) {
        if (prevProps.genderRedux !== this.props.genderRedux) {
            let arrGender = this.props.genderRedux;
            this.setState({
                genderArray: arrGender,
                gender: arrGender && arrGender.length > 0 ? arrGender[0].key : ''
            })
        };

        if (prevProps.roleRedux !== this.props.roleRedux) {
            let arrRole = this.props.roleRedux;
            this.setState({
                roleArray: arrRole,
                role: arrRole && arrRole.length > 0 ? arrRole[0].key : ''
            })
        }
    };

    handleOnChangeImage = (event) => {
        let fileImage = event.target.files;
        let file = fileImage[0];
        if (file) {
            let imageObjectUrl = URL.createObjectURL(file);
            this.setState({
                previewImageURL: imageObjectUrl
            })
        }
    };

    isOpenImage = () => {
        if (!this.state.previewImageURL) return;

        this.setState({ isOpen: true });
    };

    handleCreateUser = () => {
        let isValid = this.checkValidateInput();
        if (isValid === false) return;

        this.props.createNewUser({
            email: this.state.email,
            password: this.state,
            fullName: this.state.fullName,
            address: this.state.address,
            gender: this.state.gender,
            phoneNumber: this.state.phoneNumber,
            roleId: this.state.role
        });
    };
    handleOnChangeInput = (event, id) => {
        let copyState = { ...this.state };
        copyState[id] = event.target.value;
        this.setState({ ...copyState });
    };

    checkValidateInput = () => {
        let isValid = true;
        let arrCheck = ['email', 'password', 'fullName', 'phoneNumber', 'address'];
        for (let i = 0; i <= arrCheck.length; i++) {
            if (!this.state[arrCheck[i]]) {
                isValid = false;
                alert('This input is required: ' + arrCheck[i]);
                break;
            }
        }
        return isValid;
    };

    render() {
        let genders = this.state.genderArray;
        let roles = this.state.roleArray;
        let language = this.props.language;
        let isLoadingGender = this.props.isLoadingGender;

        let { email, password, fullName, phoneNumber, address, gender, roleId, avatar } = this.state;
        console.log('check state', this.state);

        return (
            <div className='user-redux-container'>
                <div className="title" >USER MANAGEMENT USING REDUX</div>
                <div className='container'>
                    <div className='row'>
                        <div className='col-12 my-3'><FormattedMessage id="manage-user.add" /></div>
                        <div className='col-3'>
                            <label><FormattedMessage id="manage-user.email" /></label>
                            <input className='form-control' type='email' value={email}
                                onChange={(event) => this.handleOnChangeInput(event, 'email')} />
                        </div>
                        <div className='col-3'>
                            <label><FormattedMessage id="manage-user.password" /></label>
                            <input className='form-control' type='password' value={password}
                                onChange={(event) => this.handleOnChangeInput(event, 'password')} />
                        </div>
                        <div className='col-3'>
                            <label><FormattedMessage id="manage-user.fullname" /></label>
                            <input className='form-control' type='text' value={fullName}
                                onChange={(event) => this.handleOnChangeInput(event, 'fullName')} />
                        </div>
                        <div className='col-3'>
                            <label><FormattedMessage id="manage-user.phonenumber" /></label>
                            <input className='form-control' type='text' value={phoneNumber}
                                onChange={(event) => this.handleOnChangeInput(event, 'phoneNumber')} />
                        </div>
                        <div className='col-8'>
                            <label><FormattedMessage id="manage-user.address" /></label>
                            <input className='form-control' type='text' value={address}
                                onChange={(event) => this.handleOnChangeInput(event, 'address')} />
                        </div>
                        <div className='col-4'>
                            <label><FormattedMessage id="manage-user.gender" /></label>
                            <select className="form-control" value={gender}
                                onChange={(event) => this.handleOnChangeInput(event, 'gender')}>
                                {genders && genders.length > 0 &&
                                    genders.map((item, index) => {
                                        return (
                                            <option key={index} value={item.key}>
                                                {language === languages.VI ? item.valueVi : item.valueEn}
                                            </option>
                                        )
                                    })
                                }
                            </select>
                        </div>
                        <div className='col-8'>
                            <label><FormattedMessage id="manage-user.image" /></label>
                            <div className='preview-img-container'>
                                <input id='previewImg' type='file' hidden onChange={(event) => this.handleOnChangeImage(event)} />
                                <label className='label-upload' htmlFor="previewImg">Tải ảnh <i className='fas fa-upload'></i></label>
                                <div
                                    className='preview-image'
                                    style={{ backgroundImage: `url(${this.state.previewImageURL})` }}
                                    onClick={() => this.isOpenImage()}
                                ></div>
                            </div>
                        </div>
                        <div className='col-4'>
                            <label><FormattedMessage id="manage-user.roleid" /></label>
                            <select className="form-control" value={roleId}
                                onChange={(event) => this.handleOnChangeInput(event, 'roleId')}>
                                {roles && roles.length > 0 &&
                                    roles.map((item, index) => {
                                        return (
                                            <option key={index} value={item.key}>
                                                {language === languages.VI ? item.valueVi : item.valueEn}
                                            </option>
                                        )
                                    })
                                }
                            </select>
                        </div>
                        <div className='col-12 mt-3'>
                            <button
                                className='btn btn-primary'
                                onClick={() => this.handleCreateUser()}
                            ><FormattedMessage id="manage-user.create" /></button>
                        </div>
                    </div>
                </div>
                {this.state.isOpen === true &&
                    <LightBox
                        mainSrc={this.state.previewImageURL}
                        onCloseRequest={() => this.setState({ isOpen: false })}
                    />
                }
            </div>
        )
    }

}

const mapStateToProps = state => {
    return {
        language: state.app.language,
        genderRedux: state.admin.genders,
        roleRedux: state.admin.roles,
        isLoadingGender: state.admin.isLoadingGender
    };
};

const mapDispatchToProps = dispatch => {
    return {
        getGenderStart: () => dispatch(actions.fetchGenderStart()),
        getRoleStart: () => dispatch(actions.fetchRoleStart()),
        createNewUser: (data) => dispatch(actions.createUserStart(data)),

        //processLogout: () => dispatch(actions.processLogout()),
        //changeLanguageAppRedux: (language) => dispatch(actions.changeLanguageApp(language))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(UserRedux);
