import React, { Component } from 'react';
import { connect } from "react-redux";
import HomeHeader from '../HomeHeader';
import './PostsDetail.scss'
import HomeFooter from '../HomeFooter';
import ProductRecommend from './ProductRecommend';
import * as actions from '../../../store/actions';

class PostsDetail extends Component {

    constructor(props) {
        super(props);
        this.state = {
            product: ''
        }
    }

    componentDidMount() {
        if (this.props.match && this.props.match.params && this.props.match.params.id) {
            this.props.getDetailProduct(this.props.match.params.id);
        }
    };

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (prevProps.product !== this.props.product) {
            this.setState({
                product: this.props.product
            });
        }
    }

    render() {
        let { product } = this.state;
        console.log('check id: ', this.props.match.params.id);
        let imageBase64 = '';
        if (product.image) {
            imageBase64 = new Buffer(product.image, 'base64').toString('binary');
        }
        return (
            <>
                <HomeHeader isShowBanner={false} />
                <div className='posts-detail-container'>
                    <div className='posts-name'>Bệnh của cá KOI và cách xử lý ( PART 1 )</div>
                </div>
                <ProductRecommend id={this.props.match.params.id} />
                <HomeFooter />
            </>
        );
    }
}

const mapStateToProps = state => {
    return {
        product: state.product.products,
    };
};

const mapDispatchToProps = dispatch => {
    return {
        getDetailProduct: (id) => dispatch(actions.getAllProductStart(id))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(PostsDetail);
