import React, { Component } from 'react';
import { FormattedMessage } from 'react-intl';
import { connect } from 'react-redux';
import { languages, crudActions, CommonUtils } from '../../../utils';
import * as actions from '../../../store/actions';
import LightBox from 'react-image-lightbox';
import './PostsManage.scss';
import 'react-image-lightbox/style.css';
import MarkdownIt from 'markdown-it';
import MdEditor from 'react-markdown-editor-lite';
import 'react-markdown-editor-lite/lib/index.css';

const mdParser = new MarkdownIt(/* Markdown-it options */);


class ProductManage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            contentMarkdown: '',
            contentHTML: '',
            text: '',
            postsType: '',
            title: '',
            previewImageURL: '',
            image: ''
        }
    }

    async componentDidMount() {

    };

    componentDidUpdate(prevProps, prevState, spapshot) {

    };

    handleOnChangeInput = (event, id) => {
        let copyState = { ...this.state };
        copyState[id] = event.target.value;
        this.setState({ ...copyState });
    };

    handleEditorChange = ({ html, text }) => {
        this.setState({
            contentMarkdown: text,
            contentHTML: html
        })
    };

    handleClearForm = () => {
        this.setState({
            postsType: '',
            title: '',
            contentMarkdown: ''
        })
    };

    handleOnChangeImage = async (event) => {
        let fileImage = event.target.files;
        let file = fileImage[0];
        if (file) {
            let base64 = await CommonUtils.getBase64(file);
            console.log('check base64 image: ', base64);
            let imageObjectUrl = URL.createObjectURL(file);
            this.setState({
                previewImageURL: imageObjectUrl,
                image: base64
            })
        }
    };

    isOpenImage = () => {

    };


    render() {

        let { postsType, title } = this.state;
        console.log('check input state: ', this.state);

        return (
            <div className='container'>
                <div className='row'>
                    <div className='col-12 title'>POSTS MANAGEMENT</div>
                    <div className='col-12 my-3'>Create Posts</div>
                    <div className='col-2'>
                        <label>Posts type</label>
                        <select
                            className='form-control'
                            value={postsType}
                            onChange={(event) => this.handleOnChangeInput(event, 'postsType')}
                        >
                            <option>....</option>
                            <option>1</option>
                            <option>2</option>
                        </select>
                    </div>
                    <div className='col-7'>
                        <label>Posts title</label>
                        <input
                            className='form-control'
                            type='text'
                            value={title}
                            onChange={(event) => this.handleOnChangeInput(event, 'title')}
                        ></input>
                    </div>
                    <div className='col-3'>
                        <label>Posts Image</label>
                        <div className='preview-img-container'>
                            <input id='previewImg' type='file' hidden onChange={(event) => this.handleOnChangeImage(event)} />
                            <label className='label-upload' htmlFor="previewImg">Tải ảnh <i className='fas fa-upload'></i></label>
                            <div
                                className='preview-image'
                                style={{ backgroundImage: `url(${this.state.previewImageURL})` }}
                                onClick={() => this.isOpenImage()}
                            ></div>
                        </div>
                    </div>
                    <div className='col-12'>
                        <label>Posts content</label>
                        <MdEditor
                            style={{ height: '500px' }}
                            renderHTML={text => mdParser.render(text)}
                            onChange={this.handleEditorChange}
                            value={this.state.contentMarkdown}
                        />
                    </div>
                    <div className='col-4 pt-3'>
                        <button className='btn btn-primary'>Create posts</button>
                        <button
                            className='btn btn-secondary'
                            onClick={this.handleClearForm}
                        >Clear form</button>
                    </div>
                </div>
            </div>
        )
    }

}

const mapStateToProps = state => {
    return {

    };
};

const mapDispatchToProps = dispatch => {
    return {

    };
};

export default connect(mapStateToProps, mapDispatchToProps)(ProductManage);
