import React, { Component } from 'react';
import { FormattedMessage } from 'react-intl';
import { connect } from 'react-redux';
import './TableManageProduct.scss';
import * as actions from '../../../store/actions';

class TableManageProduct extends Component {

    constructor(props) {
        super(props);
        this.state = {
            userRedux: []
        };
    };

    componentDidMount() {
        this.props.getAllUsers();
    };

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (prevProps.allUsers !== this.props.allUsers) {
            this.setState({ userRedux: this.props.allUsers })
        }
    };

    render() {
        let arrAllUsers = this.state.userRedux;
        let imgBase64 = '';

        return (
            <div className='users-container mx-0'>
                <table id='TableManageProduct'>
                    <tr>
                        <th>Product Name</th>
                        <th>Category Type</th>
                        <th>Price</th>
                        <th>Quantity</th>
                        <th>Slug</th>
                        <th>Status</th>
                        <th>Action</th>
                    </tr>
                    {arrAllUsers && arrAllUsers.length > 0 &&
                        arrAllUsers.map((item, index) => {
                            return (
                                <tr key={index}>
                                    <td>{item.email}</td>
                                    <td>{item.fullName}</td>
                                    <td>{item.address}</td>
                                    <td>{item.phoneNumber}</td>
                                    <td>{item.gender}</td>
                                    {/* <td>{item.image}</td> */}
                                    <td>
                                        <button
                                            type='button'
                                            className='btn-edit'
                                            onClick={() => this.handleEditUser(item)}
                                        ><i className='fas fa-pencil-alt'></i></button>
                                        <button
                                            onClick={() => { this.handleDeleteUser(item) }}
                                            type='button'
                                            className='btn-delete'
                                        ><i className='fas fa-trash'></i></button>
                                    </td>
                                </tr>
                            )
                        })
                    }
                </table>
            </div>
        );

    }

}

const mapStateToProps = state => {
    return {

    };
};

const mapDispatchToProps = dispatch => {
    return {

    };
};

export default connect(mapStateToProps, mapDispatchToProps)(TableManageProduct);
