import React, { Component } from 'react';
import { connect } from 'react-redux';
import { FormattedMessage } from 'react-intl';
import "./HomeFooter.scss";

class HomeFooter extends Component {

    render() {

        return (
            <div className='home-footer-container'>
                <div className='home-footer-content-left'>
                    <div className='home-footer-title'>BaiAquatic
                        <hr></hr>
                    </div>
                    <div className='home-footer-content'>
                        <h5><b>CÔNG TY TNHH MTV ĐỨC TUẤN</b></h5>
                        <h5><b><i className='fas fa-envelope">'></i>Địa chỉ: Phước An, Lộc Tiến, Phú Lộc, T.T.Huế</b></h5>
                        <h5><b>Điện thoại: (012)3456789 - Hotline: 0123456789</b></h5>
                        <h5><b>Email: bai.aqua@gmail.com</b></h5>
                    </div>
                </div>
                <div className='home-footer-content-cennter'>
                    <div className='home-footer-title'>Bản đồ
                        <hr></hr>
                    </div>
                    <div className='home-footer-content'>
                        <iframe
                            src="https://www.google.com/maps/embed?pb=!1m18!1m12!1m3!1d3830.0403983457218!2d107.97602839678959!3d16.269699900000006!2m3!1f0!2f0!3f0!3m2!1i1024!2i768!4f13.1!3m3!1m2!1s0x3141899f729832dd%3A0x848ccd7711d508d6!2zTOG7mWMgVGnhur9uLCBQaMO6IEzhu5ljLCBIdeG6vw!5e0!3m2!1sen!2s!4v1684762995784!5m2!1sen!2s"
                            title='Địa chỉ công ty'
                            width="100%"
                            height="350"
                            style={{ border: 0 }}
                            allowfullscreen=""
                            loading="lazy"
                            referrerpolicy="no-referrer-when-downgrade"
                        ></iframe>
                    </div>
                    <div className='footer-bottom'>DESIGN BY BAI_AQUATIC</div>
                </div>
                <div className='home-footer-content-right'>
                    <div className='home-footer-title'>Fanpage
                        <hr></hr>
                    </div>
                    <div className="home-footer-content">
                        <iframe
                            src="https://www.facebook.com/plugins/page.php?href=https%3A%2F%2Fwww.facebook.com%2Fadc.bai&tabs=timeline&width=340&height=500&small_header=false&adapt_container_width=true&hide_cover=false&show_facepile=true&appId"
                            width="100%"
                            height="350"
                            title='link facebook'
                            style={{ border: "none", overflow: "hidden" }}
                            scrolling="no"
                            frameborder="0"
                            allowfullscreen="true"
                            allow="autoplay; clipboard-write; encrypted-media; picture-in-picture; web-share"></iframe>
                    </div>
                </div>
            </div>
        );
    };

}

const mapStateToProps = state => {
    return {
        isLoggedIn: state.user.isLoggedIn,
        language: state.app.language
    };
};

const mapDispatchToProps = dispatch => {
    return {
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(HomeFooter);
