import React, { Component } from 'react';
import { connect } from 'react-redux';
import { push } from "connected-react-router";

import * as actions from "../../store/actions";

import './Login.scss';
import { FormattedMessage } from 'react-intl';

class Login extends Component {
    constructor(props) {
        super(props);
    }

    render() {


        return (
            <div className='login-background row'>
                <div className='col-8 login-intro'></div>
                <div className='col-4 login-form'>
                    <div className='container'>
                        <div className='login-content row'>
                            <div className='col-12 title'>Aquatic System</div>
                            <div className='col-12 signin-text text-center'>Welcome to Aquatic System</div>
                            <form className='col-12 form'>
                                <div className='form-group'>
                                    <label>Username or Email:</label>
                                    <input type='text' className='form-control' placeholder='Enter here...'></input>
                                </div>
                                <div className='form-group'>
                                    <label>Password:</label>
                                    <input type='password' className='form-control' placeholder='Enter here...'></input>
                                </div>
                                <div className='form-check'>
                                    <input type='checkbox' className='form-check-input'></input>
                                    <label className='form-check-label'>Remember login</label>
                                    <a href='' className='forgot-pwd'>Forgot your password?</a>
                                </div>
                                <div className='signin-btn'>
                                    <button type='submit' className='btn btn-secondary'>Sign in</button>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        );

    };
};

const mapStateToProps = state => {
    return {
        lang: state.app.language
    };
};

const mapDispatchToProps = dispatch => {
    return {
        navigate: (path) => dispatch(push(path)),
        adminLoginSuccess: (adminInfo) => dispatch(actions.adminLoginSuccess(adminInfo)),
        adminLoginFail: () => dispatch(actions.adminLoginFail()),
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
