            return {
                ...state,
            }
        case actionTypes.CREATE_POSTS_START:
            return {
                ...state,
            }
        case actionTypes.CREATE_POSTS_SUCCESS:
            state.message = action.message;
            return {
                ...state,
            }
        case actionTypes.CREATE_POSTS_FAILED:
            state.message = [];
            return {
                ...state,
            }
