import React, { Component } from 'react';
import { connect } from 'react-redux';
import './HomeHeader.scss';
import { FormattedMessage } from 'react-intl';

class HomeHeader extends Component {

    render() {

        return (
            <React.Fragment>
                <div className='home-header-container'>
                    <div className='home-header-content'>
                        <div className='left-content'>
                            <div className='header-bars'><i className='fas fa-bars'></i></div>
                            <div className='header-logo'>
                                <div className='logo'></div>
                                <div className='name-logo'><b>BaiAquatic</b></div>
                            </div>
                        </div>
                        <div className='center-content'>
                            <div className='child-center-content'><b><FormattedMessage id={'homeheader.homepage'} /></b></div>
                            <div className='child-center-content'><b><FormattedMessage id={'homeheader.introduction'} /></b></div>
                            <div className='child-center-content'><b><FormattedMessage id={'homeheader.design'} /></b></div>
                            <div className='child-center-content'><b><FormattedMessage id={'homeheader.category'} /></b></div>
                            <div className='child-center-content'><b><FormattedMessage id={'homeheader.shareandnew'} /></b></div>
                        </div>
                        <div className='right-content'>
                            <div className='support'><i className='fas fa-question-circle'></i><FormattedMessage id={'homeheader.support'} /></div>
                            <div className='flag'><span>VI</span></div>
                            <div className='flag'><span>EN</span></div>
                            <div className='login'><FormattedMessage id={'homeheader.login'} /></div>
                        </div>
                    </div>
                    <div className='home-header-banner'>
                        <div className='title1'><FormattedMessage id={'homeheader.title1'} /></div>
                        <div className='title2'><b><FormattedMessage id={'homeheader.title2'} /></b></div>
                        <div className='search'>
                            <i className='fas fa-search'></i>
                            <input type='text' placeholder='Tìm kiếm thông tin ...' />
                        </div>
                        <div className='download-app'>
                            <div className='google-play'>
                            </div>
                            <div className='app-store'>
                            </div>
                        </div>
                    </div>
                </div>
            </React.Fragment>
        );
    }

}

const mapStateToProps = state => {
    return {
        isLoggedIn: state.user.isLoggedIn,
        language: state.app.language
    };
};

const mapDispatchToProps = dispatch => {
    return {
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(HomeHeader);
