import React, { Component } from 'react';
import { connect } from "react-redux";
import HomeHeader from '../HomeHeader';
import './PostsDetail.scss'
import HomeFooter from '../HomeFooter';
import ProductRecommend from './ProductRecommend';
import * as actions from '../../../store/actions';

class PostsDetail extends Component {

    constructor(props) {
        super(props);
        this.state = {
            allPosts: ''
        }
    }

    componentDidMount() {
        if (this.props.match && this.props.match.params && this.props.match.params.id) {
            this.props.getDetailPosts(this.props.match.params.id);
        }
    };

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (prevProps.allPosts !== this.props.allPosts) {
            this.setState({
                allPosts: this.props.allPosts
            })
        }
    }

    render() {

        let { allPosts } = this.state;
        console.log('check state: ', allPosts);

        return (
            <>
                <HomeHeader isShowBanner={false} />
                {allPosts && allPosts.length > 0 &&
                    allPosts.map((item, index) => {
                        return (
                            <div className='posts-detail-container'>
                                <div className='posts-name'>Bệnh của cá KOI và cách xử lý ( PART 1 )</div>
                                <div className='posts-image'>
                                    <div className='image'>
                                        <div className='img'></div>
                                    </div>
                                </div>
                                <div className='post-content'>đasdsadasdasdasds</div>
                            </div>
                        )
                    })}
                <HomeFooter />
            </>
        );
    }
}

const mapStateToProps = state => {
    return {
        allPosts: state.posts.allPosts
    };
};

const mapDispatchToProps = dispatch => {
    return {
        getDetailPosts: (id) => dispatch(actions.getAllPostsStart(id))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(PostsDetail);
