/* eslint-disable react-hooks/rules-of-hooks */
import React, { Component } from 'react';
import { connect } from 'react-redux';
import { push } from "connected-react-router";

import * as actions from "../../store/actions";

import './Login.scss';
import { FormattedMessage } from 'react-intl';
import { handleLoginApi } from '../../services/userService';
import { useHistory } from 'react-router';
import { userLoginSuccess } from '../../store/actions';

class Login extends Component {
    constructor(props) {
        super(props);
        this.state = {
            username: '',
            password: '',
            isShowHidePwd: true,
            message: ''
        }
    }

    handleOnChangeUserName = (event) => {
        this.setState({
            username: event.target.value
        });
    };

    handleOnChangePwd = (event) => {
        this.setState({
            password: event.target.value
        });
    };

    handleLogin = async () => {
        this.setState({
            message: ''
        })
        try {
            let data = await handleLoginApi(this.state.username, this.state.password);
            if (data && data.errCode !== 0) {
                this.setState({
                    message: data.errMessage
                })
            }
            if (data && data.errCode === 0) {
                this.props.userLoginSuccess(data.user)
                console.log('login success!')
            }
        } catch (e) {
            if (e.response) {
                if (e.response.data) {
                    this.setState({
                        message: e.response.data.errMessage
                    })
                }
            }

        }
    };

    handleShowHidePwd = () => {
        this.setState({
            isShowHidePwd: !this.state.isShowHidePwd
        });
    };

    render() {


        return (
            <div className='login-background row'>
                <div className='col-8 login-intro'></div>
                <div className='col-4 login-form'>
                    <div className='container'>
                        <div className='login-content row'>
                            <div className='col-12 title'>Aquatic System</div>
                            <div className='col-12 signin-text text-center'>Welcome to Green Aquatic System</div>
                            <form className='col-12 form'>
                                <div className='form-group'>
                                    <label>Email:</label>
                                    <input type='text' className='form-control' placeholder='Enter here...' value={this.state.username}
                                        onChange={(event) => { this.handleOnChangeUserName(event) }}></input>
                                </div>
                                <div className='form-group'>
                                    <label>Password:</label>
                                    <div className='custom-pwd-input'>
                                        <input type={this.state.isShowHidePwd ? 'password' : 'text'} className='form-control' placeholder='Enter here...' value={this.state.password}
                                            onChange={(event) => { this.handleOnChangePwd(event) }}></input>
                                        <span onClick={() => { this.handleShowHidePwd() }}><i className={this.state.isShowHidePwd ? 'far fa-eye-slash' : 'far fa-eye'}></i></span>
                                    </div>
                                </div>
                                <div className='form-check'>
                                    <input type='checkbox' className='form-check-input'></input>
                                    <label className='form-check-label'>Remember login</label>
                                    <span className='forgot-pwd'>Forgot your password?</span>
                                </div>
                                <div className='' style={{ color: 'red' }}>{this.state.message}</div>
                                <div className='signin-btn'>
                                    <button type='button' className='btn btn-success signin' onClick={() => this.handleLogin()}>Login</button>
                                </div>
                                <div className='signin-other row'>
                                    <div className='col-5 line-1'><hr></hr></div>
                                    <div className='col-2 text-center text-other'>Or</div>
                                    <div className='col-5 line-2'><hr></hr></div>
                                </div>
                                <div className='signin-google'>
                                    <i className='fab fa-google-plus-g google-icon'></i>
                                    <p className='google-text'>Login with Google</p>
                                </div>
                                <div className='signin-fb'>
                                    <i className='fab fa-facebook-f fb-icon'></i>
                                    <p className='fb-text'>Login with Facebook</p>
                                </div>
                                <div className='create-acc'>
                                    <label>You not have an account?</label>
                                    <span className='register'>Register now</span>
                                </div>
                            </form>
                        </div>
                    </div>
                </div>
            </div>
        );

    };
};

const mapStateToProps = state => {
    return {
        lang: state.app.language
    };
};

const mapDispatchToProps = dispatch => {
    return {
        navigate: (path) => dispatch(push(path)),
        //userLoginFail: () => dispatch(actions.userLoginFail()),
        userLoginSuccess: (userInfo) => dispatch(actions.userLoginSuccess(userInfo))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Login);
