import React, { Component } from 'react';
import { FormattedMessage } from 'react-intl';
import { connect } from 'react-redux';
import './UserManage.scss';
import { handleGetAllUsers, createNewUserService } from '../../services/userService';
import ModalUser from './ModalUser';

class UserManage extends Component {

    constructor(props) {
        super(props);
        this.state = {
            arrUsers: [],
            isOpenModalUser: false,
        };
    };

    async componentDidMount() {
        await this.getUser();
    };

    getUser = async () => {
        let response = await handleGetAllUsers('ALL');
        console.log(`get user from nodejs`, response);
        if (response && response.errCode === 0) {
            this.setState({
                arrUsers: response.users
            })
        };
    }

    handleAddNewUser = () => {
        this.setState({
            isOpenModalUser: true,
        })
    };

    toggleUserModal = () => {
        this.setState({
            isOpenModalUser: !this.state.isOpenModalUser,
        })
    };

    createNewUser = (data) => {
        try {
            createNewUserService(data);
        } catch (e) {
            console.log(e)
        }
        console.log('check data: ', data);
    };

    render() {
        let arrUsers = this.state.arrUsers;
        return (
            <div className='users-container'>
                <ModalUser
                    isOpen={this.state.isOpenModalUser}
                    toggleUser={this.toggleUserModal}
                    createNewUser={this.createNewUser}
                />
                <div className="text-center title">User Manage</div>
                <div className='add-user'>
                    <button
                        className='btn btn-primary px-3'
                        onClick={() => { this.handleAddNewUser() }}
                    ><i className='fas fa-plus icon'></i>Add a new user
                    </button>
                </div>
                <div className='users-table'>
                    <table id="customers">
                        <tr>
                            <th>Email</th>
                            <th>Full Name</th>
                            <th>Adress</th>
                            <th>Phone Number</th>
                            <th>Gender</th>
                            <th>Image</th>
                            <th>Action</th>
                        </tr>
                        {arrUsers && arrUsers.map((item, index) => {
                            return (
                                <>
                                    <tr>
                                        <td>{item.email}</td>
                                        <td>{item.fullName}</td>
                                        <td>{item.address}</td>
                                        <td>{item.phoneNumber}</td>
                                        <td>{item.gender}</td>
                                        <td>{item.image}</td>
                                        <td>
                                            <button type='button' className='btn-edit'><i className='fas fa-pencil-alt'></i></button>
                                            <button type='button' className='btn-delete'><i className='fas fa-trash'></i></button>
                                        </td>
                                    </tr>
                                </>
                            )
                        })}
                    </table>
                </div>
            </div>
        );
    }

}

const mapStateToProps = state => {
    return {
    };
};

const mapDispatchToProps = dispatch => {
    return {
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(UserManage);
