import React, { Component } from 'react';
import { connect } from 'react-redux';
import './Product.scss';
import { FormattedMessage } from 'react-intl';
import * as actions from '../../../store/actions';

class Product extends Component {
    constructor(props) {
        super(props);
        this.state = {
            products: []
        }
    }

    componentDidMount() {
        this.props.getAllProduct();
    };

    componentDidUpdate(prevProps, prevState, snapshot) {
        if (prevProps.products !== this.props.products) {
            this.setState({ products: this.props.products })
        }
    };

    render() {
        let { products } = this.state;
        console.log('check state getAllProduct: ', products);

        return (
            <div className='section-share product-section'>
                <div className='section-container'>
                    <div className='section-header'>
                        <span className='title-section'>Sản Phẩm</span>
                        <button className='btn-section'>Xem tất cả</button>
                    </div>
                    <div className='section-content'>
                        <div className='row'>
                            <div className='col-3'>
                                {products && products.length > 0 &&
                                    <div className='product'>
                                        <div className='img-product'>
                                        </div>
                                        <div className='name-product'><span>Sản phẩm thứ nhất đầu tiên</span></div>
                                        <div className='price'>
                                            <div className='price-product'>100.000.000đ</div>
                                            <div className='da-ban'>Đã bán: 90</div>
                                        </div>
                                    </div>
                                }
                            </div>
                            <div className='col-12'>
                                <div className='paging'>
                                    <nav aria-label="Page navigation example">
                                        <ul className="pagination">
                                            <li className="page-item">
                                                <a className="page-link" href="#" aria-label="Previous">
                                                    <span aria-hidden="true">&laquo;</span>
                                                    <span class="sr-only">Previous</span>
                                                </a>
                                            </li>
                                            <li className="page-item"><a class="page-link" href="#">1</a></li>
                                            <li className="page-item"><a class="page-link" href="#">2</a></li>
                                            <li className="page-item"><a class="page-link" href="#">3</a></li>
                                            <li className="page-item">
                                                <a className="page-link" href="#" aria-label="Next">
                                                    <span aria-hidden="true">&raquo;</span>
                                                    <span className="sr-only">Next</span>
                                                </a>
                                            </li>
                                        </ul>
                                    </nav>
                                </div>
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        );
    }

}

const mapStateToProps = state => {
    return {
        isLoggedIn: state.user.isLoggedIn,
        language: state.app.language,
        products: state.product.products
    };
};

const mapDispatchToProps = dispatch => {
    return {
        getAllProduct: () => dispatch(actions.getAllProductStart())
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(Product);
