import React, { Component } from 'react';
import { FormattedMessage } from 'react-intl';
import { connect } from 'react-redux';
import { Button, Modal, ModalHeader, ModalBody, ModalFooter } from 'reactstrap';


class ModalUser extends Component {

    constructor(props) {
        super(props);
        this.state = {

        }
    }

    componentDidMount() {
    }

    toggle = () => {
        this.props.toggleUser();
    };

    render() {
        return (
            <Modal
                isOpen={this.props.isOpen}
                toggle={() => { this.toggle() }}
                className={'abcClassname'}
                size='md'
            >
                <ModalHeader toggle={() => { this.toggle() }}>Create a new user</ModalHeader>
                <ModalBody>
                    <div class="container">
                        <div class="row">
                            <form action="postCRUD" method="post">
                                <div class="form-row">
                                    <div class="form-group col-md-12">
                                        <label for="inputEmail4">Email</label>
                                        <input type="email" class="form-control" name="email" placeholder="Input here..." />
                                    </div>
                                    <div class="form-group col-md-12">
                                        <label for="inputPassword4">Password</label>
                                        <input type="password" class="form-control" name="password" placeholder="Input here..." />
                                    </div>
                                </div>
                                <div class="form-group">
                                    <label for="inputName">fullName</label>
                                    <input type="text" class="form-control" name="fullName" placeholder="Input here..." />
                                </div>
                                <div class="form-group">
                                    <label for="inputAddress">Address</label>
                                    <input type="text" class="form-control" name="address" placeholder=" Input here..." />
                                </div>
                                <div class="form-group">
                                    <label for="inputPhone">Phone Number</label>
                                    <input type="text" class="form-control" name="phoneNumber" placeholder=" Input here..." />
                                </div>
                                <div class="form-row">
                                    <div class="form-check">
                                        <label class="form-check-label">
                                            Male
                                            <input type="radio" className='form-group' name="gender" value="1" />
                                        </label>
                                    </div>
                                    <div class="form-check">
                                        <label class="form-check-label">
                                            Female
                                            <input type="radio" className='form-group' name="gender" value="0" />
                                        </label>
                                    </div>
                                </div>
                            </form>
                        </div>
                    </div>
                </ModalBody>
                <ModalFooter>
                    <Button className='px-3' color="primary" onClick={() => { this.toggle() }}>
                        Create
                    </Button>{' '}
                    <Button className='px-3' color="secondary" onClick={() => { this.toggle() }}>
                        Cancel
                    </Button>
                </ModalFooter>
            </Modal>
        )
    }

}

const mapStateToProps = state => {
    return {
    };
};

const mapDispatchToProps = dispatch => {
    return {
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(ModalUser);
