import React, { Component } from 'react';
import { connect } from "react-redux";
import HomeHeader from '../HomeHeader';
import './ProductDetail.scss'
import HomeFooter from '../HomeFooter';
import ProductRecommend from './ProductRecommend';
import * as actions from '../../../store/actions';

class ProductDetail extends Component {

    constructor(props) {
        super(props);
        this.state = {

        }
    }

    componentDidMount() {
        if (this.props.match && this.props.match.params && this.props.match.params.id) {
            let id = this.props.match.params.id;
            this.props.getDetailProduct(id);
        }
    };

    componentDidUpdate(prevProps, prevState, snapshot) {

    }

    render() {
        console.log(this.props.match.params.id);
        let { product } = this.props;
        console.log('check prodcut: ', product);
        return (
            <>
                <HomeHeader isShowBanner={false} />
                <div className='product-detail-container'>
                    <div className='product'>
                        <div className='row'>
                            <div className='col-5'>
                                <div className='image-product'>
                                    <div className='img'></div>
                                </div>
                            </div>
                            <div className='col-7'>
                                <div className='info-product'>
                                    <div className='caythumuc'>link cay thu muc</div>
                                    <div className='product-name'>Tên sản phẩm asdasd asdas đá asd sadas d asd asd sad asd asd sadas dá đá</div>
                                    <div className='price'>
                                        <div className='title-price'>Giá tiền</div>
                                        <div className='price-product'>1000000đ</div>
                                    </div>
                                    <div className='quantity'>
                                        <div className='title-quantity'>Số lượng</div>
                                        <div className='form'>
                                            <button type='button' className='minus'><i className='fas fa-minus' /></button>
                                            <input type='text' className='text' role="spinbutton" aria-valuenow="1" value="1"></input>
                                            <button type='button' className='plus'><i className='fas fa-plus' /></button>
                                        </div>
                                    </div>
                                    <div className='buy'>
                                        <button className='btn btn-warning'><i className='fas fa-cart-plus' /> Thêm vào giỏ hàng</button>
                                        <button className='btn btn-danger'>Mua ngay</button>
                                    </div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className='detail-comment'>
                        <div className='row'>
                            <div className='col-5'>
                                <div className='detail'>
                                    <div className='title-detail'>Chi tiết sản phẩm</div>
                                    <div className='detail-content'>adsadsdsadadasdasdasd</div>
                                </div>
                            </div>
                            <div className='col-7'>
                                <div className='comment'>
                                    <div className='title-comment'>Đánh giá sản phẩm</div>
                                </div>
                            </div>
                        </div>
                    </div>
                    <div className='recommend'></div>
                </div>
                <ProductRecommend />
                <HomeFooter />
            </>
        );
    }
}

const mapStateToProps = state => {
    return {
        product: state.product.products
    };
};

const mapDispatchToProps = dispatch => {
    return {
        getDetailProduct: (id) => dispatch(actions.getAllProductStart(id))
    };
};

export default connect(mapStateToProps, mapDispatchToProps)(ProductDetail);
