import actionTypes from './actionTypes';
import { handleGetAllProducts, handleGetAllCodeService } from '../../services/ProductService';
import { Toast, toast } from 'react-toastify';

export const getAllProductStart = () => {
    return async (dispatch, getState) => {
        try {
            dispatch({ type: actionTypes.GET_ALL_PRODUCT_START })
            let res = await handleGetAllProducts("ALL");
            if (res && res.errCode === 0) {
                dispatch(getAllProductSuccess(res.data.reverse()));
            } else {
                dispatch(getAllProductFailed());
            }
        } catch (e) {
            dispatch(getAllProductFailed());
            console.log('getAllProductFailed err: ', e);
        }
    }
}
export const getAllProductSuccess = (data) => ({
    type: actionTypes.GET_ALL_PRODUCT_SUCCESS,
    products: data
})
export const getAllProductFailed = () => ({
    type: actionTypes.GET_ALL_PRODUCT_FAILED
})

export const getAllCodeStart = () => {
    return async (dispatch, getState) => {
        try {
            dispatch({ type: actionTypes.GET_ALL_CODE_START })
            let res = await handleGetAllCodeService();
            console.log('check res: ', res);
            if (res && res.errCode === 0) {
                dispatch(getAllCodeSuccess(res.data));
            } else {
                dispatch(getAllCodeFailed());
            }
        } catch (e) {
            dispatch(getAllCodeFailed());
            console.log('getAllCodeFailed err: ', e);
        }
    }
}
export const getAllCodeSuccess = (data) => ({
    type: actionTypes.GET_ALL_CODE_SUCCESS,
    allCodes: data
})

export const getAllCodeFailed = () => ({
    type: actionTypes.GET_ALL_CODE_FAILED
})
